          label: 'February Data',
          data: [42, 62, 58, 72],
          borderColor: '#FFEB3B', // Blurred yellow
          backgroundColor: 'rgba(255, 235, 59, 0.2)', // Soft yellow gradient
          tension: 0.4, 
